INSERT [dbo].[GameRule] ([KindID], [KindName], [KindIcon], [KindIntro], [KindRule], [Nullity], [CollectDate]) VALUES (50, N'新六人牛牛', N'/RuleIoc/9.png', N'《新六人牛牛》是网狐公司研发的一款竖版扑克牌类游戏，在民间流行的经典三张纸牌的基础上做了玩法上创新。游戏中的经典模式高度还原拼三张的玩法，每位玩家开局可以获得三张手牌，通过加注、比牌、弃牌等操作来进行玩家之间的对战。红黑大战和疯狂3+2的创新玩法，更是胆识与智慧的较量。', N'<h2 style="margin:0px 40px 20px;padding:0px 0px 5px;font-size:20px;color:#3b3b3b;border-bottom:1px solid #cccccc;font-family:tahoma, arial, &quot;Hiragino Sans GB&quot;, 宋体;">游戏截图</h2>
<p style="margin:0px 40px;padding:0px;color:#3b3b3b;font-size:16px;line-height:3;text-indent:32px;font-family:tahoma, arial, &quot;Hiragino Sans GB&quot;, 宋体;"><img src="http://www.image.com//Rules/20170829151945_2548.png" alt="" width="710" style="border:0px;display:block;margin:0px auto;" /></p>
