INSERT [dbo].[GameRule] ([KindID], [KindName], [KindIcon], [KindIntro], [KindRule], [Nullity], [CollectDate]) VALUES (391, N'广东麻将', N'/RuleIoc/0.png', N'《广东麻将》是网狐公司研发的一款麻将类游戏，目前流行的主要规则便是鸡胡、做牌两种打法。不许吃牌只许自摸，并且讲究杠牌、买马，打法简单节奏快速。在玩家A的手中只剩两对牌或者一对加两张单牌的时候，其他的任何一家打的牌，玩家A碰，单吊一张牌。如果玩家A自摸，则由被歼玩家一人全全承担。在歼牌后，玩家的自杠，也是由被歼的玩家承担。而在玩家A没歼牌之前的自杠和暗杠则由其他玩家承担。', N'<h2 style="margin:0px 40px 20px;padding:0px 0px 5px;font-size:20px;color:#3b3b3b;border-bottom:1px solid #cccccc;font-family:tahoma, arial, &quot;Hiragino Sans GB&quot;, 宋体;">游戏截图</h2>
<p style="margin:0px 40px;padding:0px;color:#3b3b3b;font-size:16px;line-height:3;text-indent:32px;font-family:tahoma, arial, &quot;Hiragino Sans GB&quot;, 宋体;"><img src="http://www.image.com//Rules/20170829151945_2548.png" alt="" width="710" style="border:0px;display:block;margin:0px auto;" /></p>
<h2 style="margin:50px 40px 20px;padding:0px 0px 5px;font-size:20px;color:#3b3b3b;border-bottom:1px solid #cccccc;font-family:tahoma, arial, &quot;Hiragino Sans GB&quot;, 宋体;">游戏介绍</h2>
